import React from "react";
import { useState } from 'react'
import FormInput from "./form_input";
import Table from "./table";
import '../App.css';
import axios from "axios";

function Main(){
 const [tableData, setTableData] = useState([])
 const [formInputData, setformInputData] = useState(
     {
     stationName:'',
     location:'',
     distance:''
    }
 );
  const [stationData, setStationData] = useState(null)
  const [streetName, setStreetName] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");

    function getStation() {
    axios({
      method: "GET",
      url:"/station/" + streetName + city + state,
    })
    .then((response) => {
      const res =response.data
      setStationData(({
        location: res.location,
        name: res.name,
        distance: res.distance
        }))
    }).catch((error) => {
      if (error.response) {
        console.log(error.response)
        console.log(error.response.status)
        console.log(error.response.headers)
        }
    })}

 const handleChange=(evnt)=>{
     const newInput = (data)=>({...data, [evnt.target.name]:evnt.target.value})
    setformInputData(newInput)
 }

 const handleSubmit= (evnt) =>{
     evnt.preventDefault();
     const checkEmptyInput = !Object.values(formInputData).every(res=>res==="")
     if(checkEmptyInput)
     {
      const newData = (data)=>([...data, formInputData])
      setTableData(newData);
      const emptyInput= {fullName:'', emailAddress:'', salary:''}
      setformInputData(emptyInput)
     }
 }
 return(

     <React.Fragment>
     <body className="App-body">
         <form>
          <label>Enter your Street name/address:
            <input
                type="text"
                value={streetName}
                onChange={(e) => setStreetName(e.target.value)}
            />
          </label>
          </form>

          <form>
          <label>Enter your city:
            <input
                type="text"
                value={city}
                onChange={(e) => setCity(e.target.value)}
            />
          </label>
          </form>

          <form>
          <label>Enter your state:
            <input
                type="text"
                value={state}
                onChange={(e) => setState(e.target.value)}
            />
          </label>
          </form>

          {/* new line start*/}
         <p>To get your location: </p><button onClick={getStation}>Click me</button>
          {stationData && <div className="container">
              <div className="row">
              <table className="table">
            <thead>
                <tr>
                    <th>Station Name</th>
                    <th>Location</th>
                    <th>Distance</th>
                </tr>
            </thead>
            <tbody>
                <td>{stationData.name}</td>
                <td>{stationData.location}</td>
                <td>{stationData.distance}</td>
            </tbody>
        </table>
            </div>
            </div>
          }
         {/* end of new line */}
    </body>
     <div className="container">
     <div className="row">
         <div className="col-sm-8">
         <FormInput handleChange={handleChange} formInputData={formInputData} handleSubmit={handleSubmit}/>
         <Table tableData={tableData}/>
         </div>
         <div className="col-sm-4">
         </div>
     </div>
    </div>
     </React.Fragment>
 );
}
export default Main;
