import { useState } from 'react'
import axios from "axios";
import logo from './car-svgrepo-com.svg';
import './App.css';
import DisplayFormDataInTable from "./data_table/display_form_data_in_table"

function App() {

   // new line start
  const [profileData, setProfileData] = useState(null)
  const [locationData, setLocationData] = useState(null)
  const [address, setAddress] = useState("");

  function getData() {
    axios({
      method: "GET",
      url:"/profile",
    })
    .then((response) => {
      const res =response.data
      setProfileData(({
        profile_name: res.name,
        about_me: res.about}))
    }).catch((error) => {
      if (error.response) {
        console.log(error.response)
        console.log(error.response.status)
        console.log(error.response.headers)
        }
    })}
    function getLocation() {
    axios({
      method: "GET",
      url:"/location/" + address,
    })
    .then((response) => {
      const res =response.data
      setLocationData(({
        location: res.location}))
    }).catch((error) => {
      if (error.response) {
        console.log(error.response)
        console.log(error.response.status)
        console.log(error.response.headers)
        }
    })}
    //end of new line
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          EV-Assist
        </p>
            <form>
      <label>Enter your address:
        <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
        />
      </label>
    </form>
        <a
          className="App-link"
          href="https://afdc.energy.gov/stations/#/find/nearest"
          target="_blank"
          rel="noopener noreferrer"
        >
          US Department of Energy Alternative Fueling Station Locator (Source)
        </a>

        {/* new line start*/}
         <p>To get your location: </p><button onClick={getLocation}>Click me</button>
        {locationData && <div>
              <p>Location: {locationData.location}</p>
            </div>
        }
         {/* end of new line */}

         {/* new line start*/}
         <DisplayFormDataInTable />
         {/* end of new line*/}
      </header>
      <body className="App-body">
      </body>
    </div>
  );
}

export default App;
